contracts/CitationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title CitationRegistry
 * @dev Tracks citations between research papers
 * This contract is responsible for recording and verifying citations,
 * as well as providing methods to query citation data.
 */
contract CitationRegistry is Ownable {
    struct Citation {
        uint256 citationId;
        uint256 citingPaperId;   // Paper that is citing
        uint256 citedPaperId;    // Paper being cited
        address citer;
        uint256 timestamp;
        bool isVerified;
    }

    // All citations by citation ID
    mapping(uint256 => Citation) public citations;

    // Maps paperId to arrays of citation IDs where it's cited
    mapping(uint256 => uint256[]) private paperCitations;

    // Maps paperId to arrays of citation IDs that it cites
    mapping(uint256 => uint256[]) private paperCites;

    // Tracks the total number of citations
    uint256 private nextCitationId = 1;

    // Addresses allowed to record citations (typically other contracts)
    mapping(address => bool) public citationRecorders;

    // Addresses allowed to verify citations (typically academic institutions)
    mapping(address => bool) public citationVerifiers;

    // Events
    event CitationRecorded(uint256 indexed citationId, uint256 indexed citingPaperId, uint256 indexed citedPaperId, address citer);
    event CitationVerified(uint256 indexed citationId, address verifier);
    event CitationRecorderAdded(address indexed recorder);
    event CitationRecorderRemoved(address indexed recorder);
    event CitationVerifierAdded(address indexed verifier);
    event CitationVerifierRemoved(address indexed verifier);

    // Modifiers
    modifier onlyCitationRecorder() {
        require(citationRecorders[msg.sender] || msg.sender == owner(), "Not authorized to record citations");
        _;
    }

    modifier onlyCitationVerifier() {
        require(citationVerifiers[msg.sender] || msg.sender == owner(), "Not authorized to verify citations");
        _;
    }

    /**
     * @dev Records a citation between papers
     * @param _citingPaperId The ID of the paper doing the citing
     * @param _citedPaperId The ID of the paper being cited
     * @param _citer Address of the account creating the citation
     * @return citationId The ID of the newly recorded citation
     */
    function recordCitation(
        uint256 _citingPaperId, 
        uint256 _citedPaperId, 
        address _citer
    ) external onlyCitationRecorder returns (uint256) {
        require(_citingPaperId != _citedPaperId, "Cannot cite self");

        // Create the citation
        uint256 citationId = nextCitationId++;

        citations[citationId] = Citation({
            citationId: citationId,
            citingPaperId: _citingPaperId,
            citedPaperId: _citedPaperId,
            citer: _citer,
            timestamp: block.timestamp,
            isVerified: false
        });

        // Update the citation lists
        paperCitations[_citedPaperId].push(citationId);
        paperCites[_citingPaperId].push(citationId);

        emit CitationRecorded(citationId, _citingPaperId, _citedPaperId, _citer);

        return citationId;
    }

    /**
     * @dev Verifies a citation
     * @param _citationId The ID of the citation to verify
     */
    function verifyCitation(uint256 _citationId) external onlyCitationVerifier {
        require(_citationId > 0 && _citationId < nextCitationId, "Citation does not exist");
        require(!citations[_citationId].isVerified, "Citation already verified");

        citations[_citationId].isVerified = true;

        emit CitationVerified(_citationId, msg.sender);
    }

    /**
     * @dev Get citation count for a paper
     * @param _paperId The ID of the paper
     * @return The number of times the paper has been cited
     */
    function getCitationCount(uint256 _paperId) external view returns (uint256) {
        return paperCitations[_paperId].length;
    }

    /**
     * @dev Get verified citation count for a paper
     * @param _paperId The ID of the paper
     * @return The number of verified citations for the paper
     */
    function getVerifiedCitationCount(uint256 _paperId) external view returns (uint256) {
        uint256 count = 0;
        uint256[] memory citationIds = paperCitations[_paperId];

        for (uint256 i = 0; i < citationIds.length; i++) {
            if (citations[citationIds[i]].isVerified) {
                count++;
            }
        }

        return count;
    }

    /**
     * @dev Get all citations for a paper
     * @param _paperId The ID of the paper
     * @return An array of citation IDs where the paper is cited
     */
    function getCitations(uint256 _paperId) external view returns (uint256[] memory) {
        return paperCitations[_paperId];
    }

    /**
     * @dev Get all papers cited by a paper
     * @param _paperId The ID of the paper
     * @return An array of citation IDs that the paper cites
     */
    function getCitedPapers(uint256 _paperId) external view returns (uint256[] memory) {
        return paperCites[_paperId];
    }

    /**
     * @dev Get details about a specific citation
     * @param _citationId The ID of the citation
     * @return citingPaperId The ID of the paper doing the citing
     * @return citedPaperId The ID of the paper being cited
     * @return citer Address of the account that created the citation
     * @return timestamp When the citation was recorded
     * @return isVerified Whether the citation has been verified
     */
    function getCitationDetails(uint256 _citationId) external view returns (
        uint256 citingPaperId,
        uint256 citedPaperId,
        address citer,
        uint256 timestamp,
        bool isVerified
    ) {
        require(_citationId > 0 && _citationId < nextCitationId, "Citation does not exist");
        Citation memory citation = citations[_citationId];

        return (
            citation.citingPaperId,
            citation.citedPaperId,
            citation.citer,
            citation.timestamp,
            citation.isVerified
        );
    }

    /**
     * @dev Add an address to the list of authorized citation recorders
     * @param _recorder Address to authorize
     */
    function addCitationRecorder(address _recorder) external onlyOwner {
        require(_recorder != address(0), "Invalid address");
        require(!citationRecorders[_recorder], "Already a citation recorder");

        citationRecorders[_recorder] = true;

        emit CitationRecorderAdded(_recorder);
    }

    /**
     * @dev Remove an address from the list of authorized citation recorders
     * @param _recorder Address to remove authorization from
     */
    function removeCitationRecorder(address _recorder) external onlyOwner {
        require(citationRecorders[_recorder], "Not a citation recorder");

        citationRecorders[_recorder] = false;

        emit CitationRecorderRemoved(_recorder);
    }

    /**
     * @dev Add an address to the list of authorized citation verifiers
     * @param _verifier Address to authorize
     */
    function addCitationVerifier(address _verifier) external onlyOwner {
        require(_verifier != address(0), "Invalid address");
        require(!citationVerifiers[_verifier], "Already a citation verifier");

        citationVerifiers[_verifier] = true;

        emit CitationVerifierAdded(_verifier);
    }

    /**
     * @dev Remove an address from the list of authorized citation verifiers
     * @param _verifier Address to remove authorization from
     */
    function removeCitationVerifier(address _verifier) external onlyOwner {
        require(citationVerifiers[_verifier], "Not a citation verifier");

        citationVerifiers[_verifier] = false;

        emit CitationVerifierRemoved(_verifier);
    }
}

contracts/interfaces/IOriginProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IOriginProtocol
 * @dev Interface for Camp Network's Origin Protocol
 * This interface defines the methods needed to interact with Camp Network's Origin Protocol
 * for asset registration, royalty distribution, ownership verification, and provenance tracking.
 */
interface IOriginProtocol {
    /**
     * @dev Registers an asset with the Origin Protocol
     * @param assetId The ID of the asset to register
     * @param creator The address of the asset creator/owner
     */
    function registerAsset(uint256 assetId, address creator) external;

    /**
     * @dev Distributes royalty payments for an asset
     * @param assetId The ID of the asset
     * @param recipient The address receiving the royalty payment
     * @param amount The amount to distribute
     */
    function distributeRoyalty(uint256 assetId, address recipient, uint256 amount) external;

    /**
     * @dev Records royalty payments for an asset without transferring funds
     * @param assetId The ID of the asset
     * @param recipient The address that received the royalty
     * @param amount The amount paid
     */
    function recordRoyaltyPayment(uint256 assetId, address recipient, uint256 amount) external;

    /**
     * @dev Verifies if an address is the owner of an asset
     * @param assetId The ID of the asset to verify
     * @param claimedOwner The address claiming ownership
     * @return isOwner True if the address is the owner, false otherwise
     */
    function verifyOwnership(uint256 assetId, address claimedOwner) external view returns (bool isOwner);

    /**
     * @dev Gets usage rights information for an asset
     * @param assetId The ID of the asset
     * @return licenseType The type of license (e.g., "CC-BY", "All Rights Reserved")
     * @return licenseURI URI pointing to the full license details
     */
    function getUsageRights(uint256 assetId) external view returns (string memory licenseType, string memory licenseURI);

    /**
     * @dev Gets provenance information for an asset
     * @param assetId The ID of the asset
     * @return creators Array of creator addresses
     * @return timestamps Array of timestamps for each creation/modification event
     * @return descriptions Array of descriptions for each creation/modification event
     */
    function getProvenance(uint256 assetId) external view returns (
        address[] memory creators,
        uint256[] memory timestamps,
        string[] memory descriptions
    );
}

contracts/mocks/MockOriginProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "../interfaces/IOriginProtocol.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockOriginProtocol
 * @dev Mock implementation of the Camp Network's Origin Protocol interface
 * This contract simulates the behavior of the Origin Protocol for testing and development
 */
contract MockOriginProtocol is IOriginProtocol, Ownable {
    struct Asset {
        address creator;
        uint256 registrationTime;
        bool exists;
        string licenseType;
        string licenseURI;
    }

    struct ProvenanceRecord {
        address[] creators;
        uint256[] timestamps;
        string[] descriptions;
    }

    struct RoyaltyRecord {
        address recipient;
        uint256 amount;
        uint256 timestamp;
    }

    mapping(uint256 => Asset) private assets;
    mapping(uint256 => ProvenanceRecord) private provenanceRecords;
    mapping(uint256 => RoyaltyRecord[]) private royaltyRecords;

    event AssetRegistered(uint256 indexed assetId, address indexed creator, uint256 timestamp);
    event RoyaltyDistributed(uint256 indexed assetId, address indexed recipient, uint256 amount);
    event RoyaltyRecorded(uint256 indexed assetId, address indexed recipient, uint256 amount);

    /**
     * @dev Registers an asset with the Origin Protocol
     * @param assetId The ID of the asset to register
     * @param creator The address of the asset creator/owner
     */
    function registerAsset(uint256 assetId, address creator) external override {
        require(!assets[assetId].exists, "Asset already registered");

        assets[assetId] = Asset({
            creator: creator,
            registrationTime: block.timestamp,
            exists: true,
            licenseType: "All Rights Reserved",
            licenseURI: ""
        });

        // Initialize provenance record
        address[] memory creators = new address[](1);
        creators[0] = creator;

        uint256[] memory timestamps = new uint256[](1);
        timestamps[0] = block.timestamp;

        string[] memory descriptions = new string[](1);
        descriptions[0] = "Asset registration";

        provenanceRecords[assetId] = ProvenanceRecord({
            creators: creators,
            timestamps: timestamps,
            descriptions: descriptions
        });

        emit AssetRegistered(assetId, creator, block.timestamp);
    }

    /**
     * @dev Distributes royalty payments for an asset
     * @param assetId The ID of the asset
     * @param recipient The address receiving the royalty payment
     * @param amount The amount to distribute
     */
    function distributeRoyalty(uint256 assetId, address recipient, uint256 amount) external override {
        require(assets[assetId].exists, "Asset does not exist");

        // Record the royalty
        royaltyRecords[assetId].push(RoyaltyRecord({
            recipient: recipient,
            amount: amount,
            timestamp: block.timestamp
        }));

        emit RoyaltyDistributed(assetId, recipient, amount);
    }

    /**
     * @dev Records royalty payments for an asset without transferring funds
     * @param assetId The ID of the asset
     * @param recipient The address that received the royalty
     * @param amount The amount paid
     */
    function recordRoyaltyPayment(uint256 assetId, address recipient, uint256 amount) external override {
        require(assets[assetId].exists, "Asset does not exist");

        royaltyRecords[assetId].push(RoyaltyRecord({
            recipient: recipient,
            amount: amount,
            timestamp: block.timestamp
        }));

        emit RoyaltyRecorded(assetId, recipient, amount);
    }

    /**
     * @dev Verifies if an address is the owner of an asset
     * @param assetId The ID of the asset to verify
     * @param claimedOwner The address claiming ownership
     * @return isOwner True if the address is the owner, false otherwise
     */
    function verifyOwnership(uint256 assetId, address claimedOwner) external view override returns (bool isOwner) {
        return assets[assetId].exists && assets[assetId].creator == claimedOwner;
    }

    /**
     * @dev Gets usage rights information for an asset
     * @param assetId The ID of the asset
     * @return licenseType The type of license
     * @return licenseURI URI pointing to the full license details
     */
    function getUsageRights(uint256 assetId) external view override returns (string memory licenseType, string memory licenseURI) {
        require(assets[assetId].exists, "Asset does not exist");
        return (assets[assetId].licenseType, assets[assetId].licenseURI);
    }

    /**
     * @dev Gets provenance information for an asset
     * @param assetId The ID of the asset
     * @return creators Array of creator addresses
     * @return timestamps Array of timestamps for each creation/modification event
     * @return descriptions Array of descriptions for each creation/modification event
     */
    function getProvenance(uint256 assetId) external view override returns (
        address[] memory creators,
        uint256[] memory timestamps,
        string[] memory descriptions
    ) {
        require(assets[assetId].exists, "Asset does not exist");
        ProvenanceRecord storage record = provenanceRecords[assetId];
        return (record.creators, record.timestamps, record.descriptions);
    }

    /**
     * @dev Updates the license information for an asset
     * @param assetId The ID of the asset
     * @param licenseType The new license type
     * @param licenseURI The new license URI
     */
    function setLicense(uint256 assetId, string memory licenseType, string memory licenseURI) external onlyOwner {
        require(assets[assetId].exists, "Asset does not exist");
        assets[assetId].licenseType = licenseType;
        assets[assetId].licenseURI = licenseURI;
    }

    /**
     * @dev Adds a new provenance record to an asset
     * @param assetId The ID of the asset
     * @param creator The address to add to creators
     * @param description Description of the provenance event
     */
    function addProvenanceRecord(uint256 assetId, address creator, string memory description) external onlyOwner {
        require(assets[assetId].exists, "Asset does not exist");
        ProvenanceRecord storage record = provenanceRecords[assetId];

        uint256 length = record.creators.length;
        address[] memory newCreators = new address[](length + 1);
        uint256[] memory newTimestamps = new uint256[](length + 1);
        string[] memory newDescriptions = new string[](length + 1);

        for (uint256 i = 0; i < length; i++) {
            newCreators[i] = record.creators[i];
            newTimestamps[i] = record.timestamps[i];
            newDescriptions[i] = record.descriptions[i];
        }

        newCreators[length] = creator;
        newTimestamps[length] = block.timestamp;
        newDescriptions[length] = description;

        record.creators = newCreators;
        record.timestamps = newTimestamps;
        record.descriptions = newDescriptions;
    }
}

contracts/ResearchPaper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./interfaces/IOriginProtocol.sol";
import "./CitationRegistry.sol";

/**
 * @title ResearchPaper
 * @dev ERC721 token representing academic research papers
 * This contract handles paper registration, citation, verification, and royalty management.
 */
contract ResearchPaper is ERC721URIStorage, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    struct Paper {
        string ipfsHash;       // IPFS link to the paper content
        address researcher;    // Original author
        uint256 publishDate;   // Timestamp of registration
        string title;          // Paper title
        string[] keywords;     // Research keywords
        uint256 citationCount; // Number of citations (cached)
        bool isVerified;       // Verification status
        address[] coAuthors;   // Co-authors who may receive royalties
    }

    // Paper ID counter
    Counters.Counter private _tokenIds;

    // Paper metadata storage
    mapping(uint256 => Paper) public papers;

    // Keyword to paper IDs mapping for search
    mapping(string => uint256[]) private keywordPapers;

    // Researcher to paper IDs mapping
    mapping(address => uint256[]) private researcherPapers;

    // Citation registry contract
    CitationRegistry public citationRegistry;

    // Origin Protocol for IP registration and verification
    IOriginProtocol public originProtocol;

    // Verifier addresses that can verify papers
    mapping(address => bool) public verifiers;

    // Events
    event PaperRegistered(uint256 indexed paperId, address indexed researcher, string title, string ipfsHash);
    event PaperCited(uint256 indexed citingPaperId, uint256 indexed citedPaperId, uint256 citationId);
    event PaperVerified(uint256 indexed paperId, address indexed verifier);
    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);
    event CitationRegistryUpdated(address indexed citationRegistry);

    // Modifiers
    modifier onlyVerifier() {
        require(verifiers[msg.sender] || msg.sender == owner(), "Not authorized as verifier");
        _;
    }

    modifier onlyPaperOwner(uint256 paperId) {
        require(ownerOf(paperId) == msg.sender, "Not the paper owner");
        _;
    }

    modifier paperExists(uint256 paperId) {
        require(_exists(paperId), "Paper does not exist");
        _;
    }

    /**
     * @dev Constructor initializes the contract
     * @param name Name of the ERC721 token
     * @param symbol Symbol of the ERC721 token
     * @param _originProtocol Address of the Origin Protocol contract
     */
    constructor(
        string memory name,
        string memory symbol,
        address _originProtocol
    ) ERC721(name, symbol) {
        require(_originProtocol != address(0), "Invalid Origin Protocol address");
        originProtocol = IOriginProtocol(_originProtocol);
    }

    /**
     * @dev Register a new research paper
     * @param _ipfsHash IPFS hash of the paper content
     * @param _title Title of the paper
     * @param _tokenURI URI for the paper metadata
     * @param _keywords Array of keywords for the paper
     * @param _coAuthors Array of co-author addresses (optional)
     * @return paperId The ID of the newly registered paper
     */
    function registerPaper(
        string memory _ipfsHash,
        string memory _title,
        string memory _tokenURI,
        string[] memory _keywords,
        address[] memory _coAuthors
    ) external nonReentrant returns (uint256) {
        _tokenIds.increment();
        uint256 paperId = _tokenIds.current();

        // Mint the NFT
        _safeMint(msg.sender, paperId);
        _setTokenURI(paperId, _tokenURI);

        // Store paper metadata
        papers[paperId] = Paper({
            ipfsHash: _ipfsHash,
            researcher: msg.sender,
            publishDate: block.timestamp,
            title: _title,
            keywords: _keywords,
            citationCount: 0,
            isVerified: false,
            coAuthors: _coAuthors
        });

        // Update mappings
        researcherPapers[msg.sender].push(paperId);

        // Add paper to keyword index
        for (uint i = 0; i < _keywords.length; i++) {
            keywordPapers[_keywords[i]].push(paperId);
        }

        // Register with Origin Protocol
        originProtocol.registerAsset(paperId, msg.sender);

        emit PaperRegistered(paperId, msg.sender, _title, _ipfsHash);

        return paperId;
    }

    /**
     * @dev Cite another paper
     * @param _citingPaperId The ID of the paper doing the citing
     * @param _citedPaperId The ID of the paper being cited
     * @return citationId The ID of the created citation
     */
    function citePaper(uint256 _citingPaperId, uint256 _citedPaperId) 
        external
        paperExists(_citingPaperId)
        paperExists(_citedPaperId)
        onlyPaperOwner(_citingPaperId)
        returns (uint256) 
    {
        require(address(citationRegistry) != address(0), "Citation registry not set");
        require(_citingPaperId != _citedPaperId, "Cannot cite self");

        // Record citation in the registry
        uint256 citationId = citationRegistry.recordCitation(
            _citingPaperId, 
            _citedPaperId,
            msg.sender
        );

        // Update citation count cache
        papers[_citedPaperId].citationCount++;

        emit PaperCited(_citingPaperId, _citedPaperId, citationId);

        return citationId;
    }

    /**
     * @dev Verify a paper (only callable by authorized verifiers)
     * @param _paperId The ID of the paper to verify
     */
    function verifyPaper(uint256 _paperId) 
        external
        onlyVerifier
        paperExists(_paperId)
    {
        require(!papers[_paperId].isVerified, "Paper already verified");

        papers[_paperId].isVerified = true;

        emit PaperVerified(_paperId, msg.sender);
    }

    /**
     * @dev Set the citation registry contract
     * @param _citationRegistry Address of the citation registry contract
     */
    function setCitationRegistry(address _citationRegistry) external onlyOwner {
        require(_citationRegistry != address(0), "Invalid address");
        citationRegistry = CitationRegistry(_citationRegistry);
        emit CitationRegistryUpdated(_citationRegistry);
    }

    /**
     * @dev Add a verifier
     * @param _verifier Address of the verifier to add
     */
    function addVerifier(address _verifier) external onlyOwner {
        require(_verifier != address(0), "Invalid address");
        require(!verifiers[_verifier], "Already a verifier");

        verifiers[_verifier] = true;

        emit VerifierAdded(_verifier);
    }

    /**
     * @dev Remove a verifier
     * @param _verifier Address of the verifier to remove
     */
    function removeVerifier(address _verifier) external onlyOwner {
        require(verifiers[_verifier], "Not a verifier");

        verifiers[_verifier] = false;

        emit VerifierRemoved(_verifier);
    }

    /**
     * @dev Get all papers published by a researcher
     * @param _researcher Address of the researcher
     * @return Array of paper IDs published by the researcher
     */
    function getPapersByResearcher(address _researcher) external view returns (uint256[] memory) {
        return researcherPapers[_researcher];
    }

    /**
     * @dev Get papers by keyword
     * @param _keyword Keyword to search for
     * @return Array of paper IDs with the specified keyword
     */
    function getPapersByKeyword(string memory _keyword) external view returns (uint256[] memory) {
        return keywordPapers[_keyword];
    }

    /**
     * @dev Get paper details
     * @param _paperId ID of the paper
     * @return ipfsHash IPFS hash of the paper content
     * @return researcher Author address
     * @return publishDate Timestamp of registration
     * @return title Title of the paper
     * @return citationCount Number of citations
     * @return isVerified Verification status
     */
    function getPaperDetails(uint256 _paperId) 
        external 
        view 
        paperExists(_paperId)
        returns (
            string memory ipfsHash,
            address researcher,
            uint256 publishDate,
            string memory title,
            uint256 citationCount,
            bool isVerified
        )
    {
        Paper memory paper = papers[_paperId];

        return (
            paper.ipfsHash,
            paper.researcher,
            paper.publishDate,
            paper.title,
            paper.citationCount,
            paper.isVerified
        );
    }

    /**
     * @dev Get paper keywords
     * @param _paperId ID of the paper
     * @return Array of keywords for the paper
     */
    function getPaperKeywords(uint256 _paperId) 
        external 
        view 
        paperExists(_paperId)
        returns (string[] memory)
    {
        return papers[_paperId].keywords;
    }

    /**
     * @dev Get paper co-authors
     * @param _paperId ID of the paper
     * @return Array of co-author addresses
     */
    function getPaperCoAuthors(uint256 _paperId) 
        external 
        view 
        paperExists(_paperId)
        returns (address[] memory)
    {
        return papers[_paperId].coAuthors;
    }

    /**
     * @dev Check if an address is a co-author of a paper
     * @param _paperId ID of the paper
     * @param _researcher Address to check
     * @return True if the address is a co-author
     */
    function isCoAuthor(uint256 _paperId, address _researcher) 
        external 
        view 
        paperExists(_paperId)
        returns (bool)
    {
        if (papers[_paperId].researcher == _researcher) {
            return true;
        }

        address[] memory coAuthors = papers[_paperId].coAuthors;
        for (uint i = 0; i < coAuthors.length; i++) {
            if (coAuthors[i] == _researcher) {
                return true;
            }
        }

        return false;
    }
}

